const msg = $input.first().json.body.mensagem || $json.query || '';
if (!msg || typeof msg !== 'string') {
  return [{ error: 'Mensagem ausente. Envie {"mensagem":"..."}.' }];
}
return [{ mensagem: msg.trim().slice(0, 2000) }];

const s = $input.first().json.output || '';
try {
  const obj = JSON.parse(s);
  return [obj];
} catch (e) {
  return [{ error: 'Modelo n√£o retornou JSON v√°lido.', raw: s }];
}

// Espera receber algo como { params: { nome, limit } } no body
const raw  = ($json && typeof $json === 'object') ? $json : {};
const body = (raw.body && typeof raw.body === 'object') ? raw.body : raw;

const p = body.params || {};

const nome  = typeof p.nome === 'string' ? p.nome.trim() : '';
let   limit = Number.isFinite(Number(p.limit)) ? Number(p.limit) : 25;
limit = Math.min(Math.max(limit, 1), 100);

if (!nome) {
  // Retorna item marcando falha controlada
  return [{
    json: {
      ok: false,
      status: 400,
      error: 'Nome obrigat√≥rio.',
      details: { field: 'nome' }
    }
  }];
}

return [{
  json: {
    ok: true,
    params: { nome, limit }
  }
}];

// ======================= CONFIG =======================
const SOURCE_EMAIL = 'Supabase RPC search_lead_by_email';
const SOURCE_NOME  = 'Supabase RPC search_by_name';

// ======================= HELPERS ======================
function trunc(s, n = 80) {
  s = (s ?? '') + '';
  return s.length > n ? s.slice(0, n - 1) + '‚Ä¶' : s;
}

function looksLikeLead(o) {
  return o && typeof o === 'object' && (
    o.id != null || o.lead_id != null || o.full_name != null || o.email != null
  );
}

// Devolve {rows, error, unexecuted}
function takeOutcomeFrom(nodeName) {
  try {
    const its = $items(nodeName); // lan√ßa se n√£o executou
    if (!Array.isArray(its) || its.length === 0) {
      return { rows: [], error: null, unexecuted: false };
    }
    // Split Off + json = array
    if (Array.isArray(its[0].json)) {
      return { rows: its[0].json, error: null, unexecuted: false };
    }
    // Split On ‚Üí cada item √© uma linha
    const arr = its.map(i => i.json);
    const first = its[0]?.json ?? {};
    const errText = first.error || first.message || null;
    return { rows: arr, error: errText, unexecuted: false };
  } catch (e) {
    const msg = String(e?.message || e);
    const isUnexecuted = /Referenced node is unexecuted/i.test(msg);
    return { rows: [], error: isUnexecuted ? null : msg, unexecuted: isUnexecuted };
  }
}

// Normaliza e descarta linhas vazias
function normalizeRow(r) {
  if (!r || typeof r !== 'object') return null;
  const norm = {
    id:        r.id ?? r.lead_id ?? r.bubble_id ?? null,
    lead_id:   r.lead_id ?? null,
    full_name: typeof r.full_name === 'string' ? r.full_name.trim() : (r.full_name ?? null),
    email:     typeof r.email === 'string' ? r.email.trim() : (r.email ?? null),
    owner:     r.owner ?? null,
    country_iso2: r.country_iso2 ?? null,
    source:    r.source ?? null,
    notes:     r.notes ?? null,
    created_at:r.created_at ?? null,
  };
  const meaningful = norm.id || norm.lead_id || norm.full_name || norm.email;
  return meaningful ? norm : null;
}

// =================== ENTRADA (para meta) ===================
const allIn   = $input.all().map(i => i.json);
const firstIn = allIn[0] || {};
const nome    = firstIn?.params?.nome  ?? firstIn?.payload?.nome  ?? null;
const email   = firstIn?.params?.email ?? firstIn?.payload?.email ?? null;

// ================== COLETA / SELE√á√ÉO RPC ==================
const outEmail = takeOutcomeFrom(SOURCE_EMAIL);
const outNome  = takeOutcomeFrom(SOURCE_NOME);

let pickedNode = null;
let rows = [];
let rpcError = null;

if ((outEmail.rows?.length || 0) > 0) {
  pickedNode = SOURCE_EMAIL;
  rows = outEmail.rows;
} else if ((outNome.rows?.length || 0) > 0) {
  pickedNode = SOURCE_NOME;
  rows = outNome.rows;
} else {
  rpcError = outEmail.error || outNome.error || null;
  rows = [];
}

// Fallback do input **somente** se tiver cara de lead
if (rows.length === 0) {
  if (Array.isArray(firstIn) && firstIn.some(looksLikeLead)) rows = firstIn;
  else if (Array.isArray(firstIn.items) && firstIn.items.some(looksLikeLead)) rows = firstIn.items;
}

// ==================== INTENT ======================
let intent = 'buscar_pessoa_por_nome';
if (pickedNode === SOURCE_EMAIL || (!!email && !nome)) {
  intent = 'buscar_pessoa_por_email';
}

// ==================== SANEAMENTO ==================
// Normaliza e remove linhas "vazias" (ex.: {})
rows = (rows || []).map(normalizeRow).filter(Boolean);

// ====================== PREVIEW ===================
const preview = rows.slice(0, 25).map((r, i) => ({
  idx: i + 1,
  id: r.id,
  lead_id: r.lead_id,
  full_name: r.full_name,
  email: r.email,
  owner: r.owner,
  country_iso2: r.country_iso2,
  source: r.source,
  notes: r.notes ? trunc(r.notes, 80) : null,
  created_at: r.created_at,
}));

// ==================== COORTE (se veio) ============
let cohort = {};
try {
  const raw = firstIn.cohort_hash;
  if (raw) cohort = typeof raw === 'string' ? JSON.parse(raw) : raw;
} catch { /* ignore */ }

// ================== STATUS & MENSAGEM =============
const noResults = rows.length === 0;
const status = noResults ? (rpcError ? 500 : 404) : 200;

const message = noResults
  ? `üîé ${intent}\n${rpcError ? 'Erro no RPC.' : 'Nenhum resultado.'} Coorte salva (pode estar vazia).`
  : `üîé ${intent}\nResultados: *${rows.length}*.\nCoorte salva.`;

// (Opcional) Texto pronto para o Webhook em modo Text
let response_text = message;
if (!noResults) {
  const list = preview
    .map(l => `- ${l.full_name || '(sem nome)'} (${l.email || 'sem email'}${l.lead_id ? ' ‚Äî ' + l.lead_id : ''})`)
    .join('\n');
  response_text += `\n\nLeads:\n${list}`;
}

// ===================== RETORNO FINAL ===============
return [{
  json: {
    ok: status === 200,
    status,
    intent,
    query: { nome, email },
    total: rows.length,
    items: rows,
    preview,
    message,
    response_text, // para usar direto no Respond to Webhook (Text)
    error: noResults ? (rpcError ? 'Falha na consulta ao RPC.' : 'Nenhum lead encontrado para a consulta.') : null,
    cause: rpcError || null,
    cohort: {
      key:  cohort?.key ?? cohort?.name ?? null,
      ids:  cohort?.ids ?? [],
      meta: cohort?.meta ?? null,
    },
  }
}];

const hash = $json.propertyName ?? {};
const items = Object.values(hash)
  .map(v => {
    if (typeof v === 'string') {
      try { return JSON.parse(v); } catch { return null; }
    }
    return (v && typeof v === 'object') ? v : null;
  })
  .filter(Boolean);

return {
  message: `Coorte atual: ${items.length} lead(s).`,
  preview_ids: items.flatMap(x => x.ids ?? []),
  saved_at: items.at(-1)?.meta?.saved_at ?? null,
  cursor:    items.at(-1)?.meta?.cursor    ?? null,
};

const leadId = $json.lead_id ?? $json.cohort?.ids?.[0];
if (!leadId) throw new Error("lead_id n√£o encontrado.");

const cohort = $json.cohort ?? {
  ids: [leadId],
  meta: {
    count: 1,
    saved_at: new Date().toISOString(),
    cursor: new Date().toISOString(),
  },
};

// IMPORTANTE: stringify por campo
return {
  cohort_hash: {
    [leadId]: JSON.stringify(cohort),
  },
};

const input = (items?.[0]?.json) ?? {};

// --- helpers ---
const get = (obj, path) => path.reduce((a, k) => (a && a[k] !== undefined ? a[k] : undefined), obj);
const emailRe = /[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i;
const extractEmail = (s) => {
  if (typeof s !== 'string') return null;
  const m = s.match(emailRe);
  return m ? m[0].toLowerCase() : null;
};

// 1) caminhos mais prov√°veis (inclui params.novo_owner)
const paths = [
  ['ownerTarget'], ['owner_target'], ['owner'], ['action_owner'],
  ['params','owner'], ['params','novo_owner'], ['params','owner_email'],
  ['params','ownerTarget'], ['params','owner_target'],
];

let candidate = null;
for (const p of paths) {
  const v = get(input, p);
  if (typeof v === 'string' && v.trim()) { candidate = v.trim(); break; }
}

let email = extractEmail(candidate);

// 2) fallback: vasculha todo o objeto e pega o primeiro e-mail,
// priorizando chaves que contenham "owner|novo|target"
if (!email) {
  const stack = [input];
  while (stack.length && !email) {
    const obj = stack.pop();
    if (obj && typeof obj === 'object') {
      for (const [k, v] of Object.entries(obj)) {
        if (typeof v === 'string') {
          const found = extractEmail(v);
          if (found) {
            if (/owner|novo|target/i.test(k)) { email = found; break; }
            if (!email) email = found; // fallback
          }
        } else if (v && typeof v === 'object') {
          stack.push(v);
        }
      }
    }
  }
}

const ok = !!email;
return [{ json: { ok, ownerTarget: email || null } }];

// === Prep Update Summary ‚Äî compat√≠vel com novo formato do Redis ===
// Entrada esperada: Merge (append) de [ Redis GET(c(o)hort), Validate Owner ]

const all = (items || []).map(it => it.json ?? {});

function parseMaybeJSON(v) {
  if (v == null) return null;
  if (typeof v === 'string') { try { return JSON.parse(v); } catch { return null; } }
  if (typeof v === 'object') return v;
  return null;
}

const idSet = new Set();
let savedAtLatest = null;
let cursorLatest = null;

function considerMeta(meta) {
  if (!meta || typeof meta !== 'object') return;
  const s = meta.saved_at;
  if (s) {
    const d = new Date(s);
    if (!isNaN(d)) {
      const cur = savedAtLatest ? new Date(savedAtLatest) : null;
      if (!cur || d > cur) savedAtLatest = d.toISOString();
    }
  }
  if (meta.cursor) cursorLatest = meta.cursor;
}

// --- Varre todos os itens vindos do Merge ---
for (const j of all) {
  // 1) Novo formato: propertyName √© um objeto { lead_xxx: "<json string>" }
  if (j.propertyName && typeof j.propertyName === 'object' && !Array.isArray(j.propertyName)) {
    for (const k of Object.keys(j.propertyName)) {
      const parsed = parseMaybeJSON(j.propertyName[k]);
      if (parsed && Array.isArray(parsed.ids)) {
        parsed.ids.forEach(id => idSet.add(id));
        considerMeta(parsed.meta);
      }
    }
  }

  // 2) Formatos antigos: value ou propertyName.value contendo JSON stringificado
  const rawA = j?.value ?? j?.propertyName?.value ?? null;
  const parsedA = parseMaybeJSON(rawA);
  if (parsedA && Array.isArray(parsedA.ids)) {
    parsedA.ids.forEach(id => idSet.add(id));
    considerMeta(parsedA.meta);
  }

  // 3) J√° veio direto como { ids, meta }
  if (Array.isArray(j.ids)) {
    j.ids.forEach(id => idSet.add(id));
    considerMeta(j.meta);
  }
}

const ids = [...idSet];
const meta = { saved_at: savedAtLatest ?? null, cursor: cursorLatest ?? null };
const count = ids.length;

// --- Descobrir ownerTarget a partir do outro item do Merge (Validate Owner) ---
let ownerTarget = null;
for (const j of all) {
  const cand =
    j.ownerTarget ?? j.owner_target ??
    j.params?.novo_owner ?? j.params?.owner ?? j.params?.owner_email ?? null;
  if (typeof cand === 'string' && cand.trim()) { ownerTarget = cand.trim().toLowerCase(); break; }
}

// --- Monta sa√≠da ---
const pending = {
  action: 'update_owner',
  owner_target: ownerTarget ?? null,
  ids,
  stats: {
    count,
    saved_at: meta.saved_at,
    cursor: meta.cursor,
  },
};

const ok = count > 0;

return [{
  json: {
    ok,
    error_code: ok ? null : 'COORTE_VAZIA',
    message: ok
      ? 'Itens prontos para atualiza√ß√£o de owner.'
      : 'A coorte atual n√£o tem itens. Fa√ßa uma busca primeiro.',
    preview: {
      count,
      saved_at: meta.saved_at,
      cursor: meta.cursor,
    },
    pending,
    owner_label: ownerTarget || 'n√£o definido',
  }
}];

const p = $json.pending;

return [{
  message:
    `Resumo da atualiza√ß√£o:\n` +
    `‚Ä¢ Owner alvo: ${p.owner_target}\n` +
    `‚Ä¢ Quantidade: ${p.stats.count}\n` +
    (p.stats.saved_at ? `‚Ä¢ Coorte salva em: ${p.stats.saved_at}\n` : '') +
    `\nDigite **confirmar** para aplicar ou **cancelar** para descartar.`,
  preview_ids: p.ids.slice(0, 25),
  instructions: 'Use: confirmar | cancelar'
}];

// L√™ o que veio do Redis GET (Key Type = String, Name = "pending")
const rawInput =
  $json?.pending ??                // quando Name = "pending"
  $json?.propertyName ??           // fallback se usou outro Name
  $json ??                         // fallback extremo
  null;

// Tenta parsear caso seja string JSON (com \")
let pending = null;
try {
  pending = (typeof rawInput === 'string') ? JSON.parse(rawInput) : rawInput;
} catch (_) {
  // fallback para casos duplamente-encapsulados (raro)
  try {
    const once = String(rawInput).replace(/^"(.*)"$/s, '$1');
    pending = JSON.parse(once);
  } catch {}
}

// Valida√ß√£o b√°sica
const ids = Array.isArray(pending?.ids) ? pending.ids : [];
const ok = !!pending && ids.length > 0;

return [{
  json: {
    ok,
    error_code: ok ? null : 'PENDING_EMPTY_OR_INVALID',
    message: ok ? 'Pending carregado.' : 'Nenhuma a√ß√£o pendente para confirmar/cancelar.',
    pending: pending || null,
    ids,
    owner_target: pending?.owner_target ?? null,
    count: ids.length,
  }
}];

// L√™ o que veio do Redis GET (Key Type = String, Name = "pending")
const rawInput =
  $json?.pending ??                // quando Name = "pending"
  $json?.propertyName ??           // fallback se usou outro Name
  $json ??                         // fallback extremo
  null;

// Tenta parsear caso seja string JSON (com \")
let pending = null;
try {
  pending = (typeof rawInput === 'string') ? JSON.parse(rawInput) : rawInput;
} catch (_) {
  // fallback para casos duplamente-encapsulados (raro)
  try {
    const once = String(rawInput).replace(/^"(.*)"$/s, '$1');
    pending = JSON.parse(once);
  } catch {}
}

// Valida√ß√£o b√°sica
const ids = Array.isArray(pending?.ids) ? pending.ids : [];
const ok = !!pending && ids.length > 0;

return [{
  json: {
    ok,
    error_code: ok ? null : 'PENDING_EMPTY_OR_INVALID',
    message: ok ? 'Pending carregado.' : 'Nenhuma a√ß√£o pendente para confirmar/cancelar.',
    pending: pending || null,
    ids,
    owner_target: pending?.owner_target ?? null,
    count: ids.length,
  }
}];

const owner = $json.owner_target ?? '(desconhecido)';
const n = ($json.ids || []).length || 0;
return [{
  message: `‚úÖ Cancelado. Nada foi alterado. (owner proposto: ${owner}, ${n} lead(s) na a√ß√£o pendente)`,
  canceled: true,
}];

