return [{
  json: {
    ok: false,
    reason: 'BUBBLE_NOT_FOUND',
    id: $json.id ?? null,
    message: `ID ${$json.id ?? '(sem id)'} n√£o localizado no Bubble. Pulando atualiza√ß√£o no Bubble.`
  }
}];

// Itens atuais (provavelmente do "Bubble: Search by lead_id")
const bubbleItems = $input.all();

// Pega o owner_target uma √∫nica vez da mensagem (node "Explode IDs")
const idsItems = $items('Explode IDs');
const OWNER_TARGET =
  idsItems?.[0]?.json?.owner_target ??
  $json.owner_target ??  // fallback se vier direto no input
  null;

return bubbleItems.map(({ json }, i) => {
  // bubble_id vindo do resultado do Bubble (ajuste conforme seu payload)
  const bubble_id =
    json._id ??
    json.results?.[0]?._id ??
    json.response?.results?.[0]?._id ??
    json.bubble_id ??
    null;

  // id correspondente (alinha pela posi√ß√£o com "Explode IDs")
  const id =
    idsItems?.[i]?.json?.id ??
    idsItems?.[i]?.json?.lead_id ??
    json.id ??
    json.lead_id ??
    null;

  if (!bubble_id) {
    return { json: { ok: false, reason: 'BUBBLE_NOT_FOUND', id, owner_target: OWNER_TARGET } };
  }

  return {
    json: {
      ok: true,
      id,
      owner_target: OWNER_TARGET, // <‚Äî sempre o mesmo da mensagem
      bubble_id,
    },
  };
});

// quantos itens entraram no loop (ou use 'extract infos')
const expected = ($items('Loop Over Items1') ?? []).length;

// √∫ltimo retorno de cada n√≥ (apenas para checar sucesso)
const supaLast = ($items('POST + merge Supabase') ?? [])[0]?.json ?? {};
const bubbleLast = ($items('Bubble: Update a thing') ?? [])[0]?.json ?? {};

// sucesso?
const supaOK =
  (supaLast.statusCode ?? supaLast.status ?? 200) >= 200 &&
  (supaLast.statusCode ?? supaLast.status ?? 200) < 300;

const bubbleOK =
  bubbleLast?.success === true ||
  bubbleLast?.updated === true ||
  !!bubbleLast?.id || !!bubbleLast?._id;

// se OK, todos foram atualizados (porque o loop completou)
const supabase_count = supaOK ? expected : 0;
const bubble_count   = bubbleOK ? expected : 0;

return [{
  json: {
    ok: true,
    message: `Leads atualizados no Supabase: ${supabase_count}. Leads atualizados no Bubble: ${bubble_count}.`,
    supabase_count,
    bubble_count,
    expected // s√≥ para confer√™ncia
  }
}];

// Op√ß√µes padr√£o: √∫ltimos 30 dias por dia
const now = new Date();
const end = now.toISOString();
const start = new Date(now.getTime() - 30*24*60*60*1000).toISOString();

const input = $json || {};

return [{
  start: $input.first().json.params.data_inicial || start,
  end:   $input.first().json.params.data_final   || end,
  granularity: (input.granularity || 'day').toLowerCase(), // 'day' | 'week' | 'month'
  owner: input.owner || null,
  source: input.source || null,
  country_iso2: input.country_iso2 || null,
  // (Opcional) guardar chave de coorte/consulta
